import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import "./Modal.css";

const API_URL = 'http://localhost:5000/students';

export default function Modal() {

  const [modal, setModal] = useState(false);
  const [formData, setFormData] = useState({ id: '', name: '', course: '', age: '', address: '', gender: '', nationality: '', religion: '' });
  const [students, setStudents] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [isEditing, setIsEditing] = useState(false);
  const [file, setFile] = useState(null);
  const [modalOpen, setModalOpen] = useState(false);



  // Fetch all students
  const fetchStudents = async () => {
    try {
      const response = await axios.get(API_URL);
      if (Array.isArray(response.data)) {
        setStudents([...response.data]); // Creating a new array ensures React updates the UI
 // Directly set state with the fetched data
      }
    } catch (error) {
      toast.error('Failed to fetch students');
    }
  };



  useEffect(() => {
    fetchStudents();
}, []); // Fetch only once when component mounts



  // Handle form change
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // Handle search change
  const handleSearchChange = (e) => {
    setSearchQuery(e.target.value);
  };

  // Handle file selection
  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  const handleUpload = async () => {
    if (!file) {
      toast.error('Please select a CSV file first.');
      return;
    }

    const formData = new FormData();
    formData.append('file', file);

    try {
      const response = await axios.post('http://localhost:5000/students/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      toast.success('CSV uploaded and processed successfully!');
      fetchStudents(); // Refresh the student list
    } catch (error) {
      toast.error('Error uploading file. Please try again.');
      console.error(error);
    }
  };



  // Prepare data for CSV export
  const csvHeaders = [
    { label: 'ID', key: 'id' },
    { label: 'Name', key: 'name' },
    { label: 'Course', key: 'course' },
    { label: 'Age', key: 'age' },
    { label: 'Address', key: 'address' },
    { label: 'Gender', key: 'gender' },
    { label: 'Nationality', key: 'nationality' },
    { label: 'Religion', key: 'religion' }
  ];

  // Filter students based on search query
  const filteredStudents = students.filter((student) =>
    student.id.toString().includes(searchQuery) ||
    student.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
    student.course.toLowerCase().includes(searchQuery.toLowerCase()) ||
    student.age.toLowerCase().includes(searchQuery.toLowerCase()) ||
    student.address.toLowerCase().includes(searchQuery.toLowerCase()) ||
    student.gender.toLowerCase().includes(searchQuery.toLowerCase()) ||
    student.nationality.toLowerCase().includes(searchQuery.toLowerCase()) ||
    student.religion.toLowerCase().includes(searchQuery.toLowerCase())
  );

  // Add new student
  const handleAddSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post(API_URL, formData);

      toast.success('Student added successfully!');

      // Fetch updated student list from backend immediately
      const updatedList = await axios.get(API_URL);

      // Force React to re-render by creating a new array reference
      setStudents([...updatedList.data]);

      // Reset form and close modal
      setFormData({ id: '', name: '', course: '', age: '', address: '', gender: '', nationality: '', religion: '' });
      setModal(false);
    } catch (error) {
      toast.error(error.response?.data?.message || 'Error adding student!');
    }
  };




  // Update existing student
  const handleEditSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.put(`${API_URL}/${formData.id}`, formData);
      toast.success('Student updated successfully!');
      fetchStudents(); // Update main student list
      setFormData({ id: '', name: '', course: '', age: '', address: '', gender: '', nationality: '', religion: '' });
      setIsEditing(false);
      setModal(false); // Close modal after update
    } catch (error) {
      toast.error('Error updating student!');
    }
};

  // Delete student
  const handleDelete = async (id) => {
    try {
      await axios.delete(`${API_URL}/${id}`);
      toast.success('Student deleted!');

      // Fetch updated student list and force re-render
      const updatedList = await axios.get(API_URL);
      setStudents([...updatedList.data]); 

    } catch (error) {
      toast.error('Error deleting student!');
    }
  };


  // Populate form for updating student
  const handleEdit = (student) => {
    setFormData(student);
    setIsEditing(true);
    setModalOpen(true); // Open the modal
  };

  const toggleModal = () => {
    setModalOpen(!modalOpen);
  };


  if(modal) {
    document.body.classList.add('active-modal')
  } else {
    document.body.classList.remove('active-modal')
  }

  const addStudentButton = () => {
    setModal(!modal);
  }

  return (
    <>
      <button onClick={toggleModal} className="btn-modal">
        Add Student
      </button>

      {modal && (
        <div className={`modal`}>
          <div onClick={toggleModal} className="overlay"></div>
          <div className="modal-content">


            <button className="close-modal" onClick={toggleModal}>
              CLOSE [X]
            </button>
      <div className="container1" style={{ textAlign: 'center' }}>
      {!isEditing ? (
        <form onSubmit={handleAddSubmit}>
          <div class="input-field">
            <input type="text" name="id" value={formData.id} onChange={handleChange} required />
            <label for="id">ID</label>
          </div>
          <div class="input-field">
            <input type="text" name="name" value={formData.name} onChange={handleChange} required />
            <label for="id">Name</label>
          </div>
          <div class="input-field">
            <input type="text" name="course" value={formData.course} onChange={handleChange} required />
            <label for="id">Course</label>
          </div>
          <div class="input-field">
            <input type="number" name="age" value={formData.age} onChange={handleChange} required />
            <label for="id">Age</label>
          </div>
          <div class="input-field">
            <input type="text" name="address" value={formData.address} onChange={handleChange} required />
            <label for="id">Address</label>
          </div>
          <div class="input-field">
            <input type="text" name="gender" value={formData.gender} onChange={handleChange} required />
            <label for="id">Gender</label>
          </div>
          <div class="input-field">
            <input type="text" name="nationality" value={formData.nationality} onChange={handleChange} required />
            <label for="id">Nationality</label>
          </div>
          <div class="input-field">
            <input type="text" name="religion" value={formData.religion} onChange={handleChange} required />
            <label for="id">Religion</label>
          </div>
          <button className="addButton" type="submit">Add Student</button>
        </form>
      ) : (
        <form className="formPopup" onSubmit={handleEditSubmit}>
          <input type="number" name="id" placeholder="ID" value={formData.id} onChange={handleChange} required disabled />
          <input type="text" name="name" placeholder="Name" value={formData.name} onChange={handleChange} required />
          <input type="text" name="course" placeholder="Course" value={formData.course} onChange={handleChange} required />
